mapper.select:
select 
from aeolos_cart
where user_id=#{userId} and productId = #{productId}

select sum(quantity) as count
from aeolos_cart
where user_id=#{userId}

mapper.update:
update aeolos_cart
set checked = #{checked}
where user_id = #{userId}

update aeolos_cart
set checked = #{checked}
where user_id = #{userId}

  and product_id=#{productId}
